app.post('/api.js/inscription', function (requete, response) {
    "use strict";
    requete.on('data', function (dataInscription) {
        inscription = new Buffer(dataInscription);
        inscription = inscription.toString('utf-8');
        inscription = JSON.parse(inscription);
        bdd.query('SELECT login FROM user WHERE login = "' + inscription.login + '"', function (error, results){
            if (error !== null) {
                reponse = {'error' : 'Un probleme est survenue lors de la verification de disponibiliter du login choisi, si cela persiste, veuillez contacter le reponsable technique de cette API', 'data': null};
                response.send(reponse);
            } else{
                if (results.length == 0) {
                    inscription.password = sha1(inscription.password + salt);
                    bdd.query('INSERT INTO user (login, password, email, genre, date, adresse, ville, pays, avatar) VALUES ("' + inscription.login + '", "' + inscription.password  + '", "' + inscription.email + '", "' + inscription.genre + '", "' + inscription.date + '", "' + inscription.adresse + '", "' + inscription.ville + '", "' + inscription.pays + '","data:image/png;base64,")', function (erreur, resultat) {
                        if (erreur !== null) {
                            reponse = {'error' : 'Un probleme est survenue lors de l"ajout de l"user dans la base de donnee, si cela persiste, veuillez contacter le reponsable technique de cette API', 'data': null};
                            response.send(reponse);
                        } else {
                            reponse = {'error' : null, 'data': null};
                            response.send(reponse);
                        }
                    });
                } else {
                    reponse = {'error' : 'Login deja pris !!!', 'data' : null};
                    response.send(reponse);
                }   
            }
        });
});
});

app.post('/api.js/connexion', function (requete, response) {
    "use strict";
    requete.on('data', function (dataConnexion) {
        connexion = new Buffer(dataConnexion);
        connexion = connexion.toString('utf-8');
        connexion = JSON.parse(connexion);
        bdd.query('SELECT * FROM user WHERE login = "' + connexion.login + '"', function (error, results) {
            if (error !== null) {
                reponse = {'error' : 'Un probleme est survenue lors de la verification du login pour la connexion, si cela persiste, veuillez contacter le reponsable technique de cette API', 'data': null};
                response.send(reponse);
            } else {
                if (results.length == 0) {
                    reponse = {'error' : 'Login introuvable !! Inscrivez-vous avant de vous connecter !!', 'data' : null};
                    response.send(reponse);
                } else {
                    connexion.password = sha1(connexion.password + salt);
                    if (connexion.password !== results[0].password) {
                        reponse = {'error' : "Bon login mais mauvais mot de passe !!", 'data' : null};
                        response.send(reponse);
                    } else {
                        token = sha1((Math.random() * 100) + salt);
                        bdd.query('UPDATE user SET token = "' + token + '" WHERE login = "' + connexion.login + '"', function (erreur, resultat) {
                            if (erreur !== null) {
                                reponse = {'error' : "Un probleme est survenue lors de l'ajout d'un token pour votre connexion, si cela persiste, veuillez contacter le responsable technique de cette API", 'data' : null};
                                response.send(reponse);
                            } else {
                                array = [{'id': results[0].id, 'login' : results[0].login, 'token' : token}];
                                reponse = {'error' : null, 'data' : array, 'token': token};
                                response.send(reponse);
                                array = [];
                            }
                        });
                    }
                }
            }
        });
});
});

