app.post('/api.js/createGame', function (requete, response) {
    "use strict";
    requete.on('data', function (dataCreateGame) {
        createGame = new Buffer(dataCreateGame);
        createGame = createGame.toString('utf-8');
        createGame = JSON.parse(createGame);
        createGameAmi = [];
        createGameAmiMatch = false;
        createGameEnCours = false;
        bdd.query('SELECT * FROM user WHERE login = "' + createGame.login + '"', function (error, results) {
            if (error !== null) {
                reponse = {'error' : 'Un probleme est survenue lors de la verification de votre login, si cela persiste, veuillez contacter le responsable technique de cette API', 'data': null};
                response.send(reponse);
            } else {
                if (results.length == 0) {
                    reponse = {'error' : "Login introuvable !!", 'data': null};
                    response.send(reponse);
                } else {
                    if (createGame.token !== results[0].token) {
                        reponse = {'error' : "Bon login mais mauvais token, au pire deconnecter votre session et reconnectez-vous pour avoir un nouveau token", 'data': null};
                        response.send(reponse);
                    } else {
                        bdd.query('SELECT user.login, amis.reponse, amis.id_receveur AS "id" FROM user INNER JOIN amis ON amis.id_receveur = user.id WHERE amis.id_sendeur = "' + results[0].id + '"', function (erreur, resultat) {
                            if (erreur !== null) {
                                reponse = {'error' : 'Un probleme est survenue lors de la verification de vos amis, si cela persiste, veuillez contacter le responsable technique de cette API', 'data': null};
                                response.send(reponse);
                            } else {
                                for (n = 0; n < resultat.length; n = n + 1) {
                                    createGameAmi.push(resultat[n]);
                                }
                                bdd.query('SELECT user.login, amis.reponse, amis.id_sendeur AS "id" FROM user INNER JOIN amis ON amis.id_sendeur = user.id WHERE amis.id_receveur = "' + results[0].id + '"', function (probleme, solution) {
                                    if (probleme !== null) {
                                        reponse = {'error' : 'Un probleme est survenue lors de la verification de vos amis, si cela persiste, veuillez contacter le responsable technique de cette API', 'data': null};
                                        response.send(reponse);
                                    } else {
                                        for (o = 0; o < solution.length; o = o + 1) {
                                            createGameAmi.push(solution[o]);
                                        }
                                    }
                                    for (p = 0; p < createGameAmi.length; p = p + 1) {
                                        if (createGameAmi[p].login == createGame.ami) {
                                            if (createGameAmi[p].reponse == 'oui') {
                                                createGameAmiMatch = true;
                                                createGameAmi = createGameAmi[p];
                                                break;
                                            }
                                        }
                                    }
                                    if (createGameAmiMatch == false) {
                                        reponse = {'error' : "Cette ami ne vous a pas accepter ou pas encore accepter !!! Veuillez attendre ou choisir un autre ami !!!", 'data': null};
                                        response.send(reponse);
                                    } else {
                                        bdd.query('SELECT id, mot FROM mots WHERE mot = "' + createGame.mot + '"', function (sorun, cevap) {
                                            if (sorun !== null) {
                                                reponse = {'error': "Un probleme est survenue lors de la verification du mot dans la base de donnee, si cela persiste, veuillez contacter le responsable technique de cette API", 'data': null};
                                                response.send(reponse);
                                            } else {
                                                if (cevap.length == 0) {
                                                    reponse = {'error': "Ce mot n'est pas repertorier dans la base de donnee !!!", 'data': null};
                                                    response.send(reponse);
                                                } else {
                                                    bdd.query('SELECT * FROM parties WHERE id_sendeur = "' + results[0].id + '" AND id_receveur = "' + createGameAmi.id + '" OR id_sendeur = "' + createGameAmi.id + '" AND id_receveur = "' + results[0].id + '"', function (problemes, reponses) {
                                                        if (reponses.length == 0) {
                                                        } else {
                                                            for (q = 0; q < reponses.length; q = q + 1) {
                                                                if (reponses[q].etat == "en cours") {
                                                                    createGameEnCours = true;
                                                                    break;
                                                                }
                                                            }
                                                            if (createGameEnCours == true) {
                                                                reponse = {'error': "Partie deja entre vous et pas encore fini !! Continuer la partie !!!", 'data': null};
                                                                response.send(reponse);
                                                            } else {
                                                                bdd.query('INSERT INTO parties (id_sendeur, id_receveur, tour, etat, id_mot, image) VALUES ("' + results[0].id + '", "' + createGameAmi.id + '", "' + results[0].id + '", "en cours", "' + cevap[0].id + '", "null")', function (sorunlar) {
                                                                    if (sorunlar !== null) {
                                                                        reponse = {'error': "Un probleme est survenue lors de l'ajout de la partie dans la base de donnee, si cela persiste, veuillez contacter le responsable technique de cette API", 'data': null};
                                                                        response.send(reponse);
                                                                    } else {
                                                                        reponse = {'error': null, 'data': null};
                                                                        response.send(reponse);
                                                                        createGameAmi = [];
                                                                    }
                                                                });
                                                            }
                                                        }
                                                    });
                                                }
                                            }
                                        });
                                    }
                                });
                            }
                        });
                    }
                }
            }
        });
});
});

